import requests
import json

from bot.utils.config import COZE_URL as url, COZE_TOKEN as token, COZE_BOT_ID as coze_id

#coze взаимодействия

def coze_request(tg_id, query, history):
    headers = {'Authorization': f"Bearer {token}",
               'Content-Type': 'application/json',
               'Accept': '*/*',
               'Host':'api.coze.com',
               'Connection':'keep-alive'}
    data = {'conversation_id':'123',
            'bot_id': str(coze_id),
            'user': str(tg_id),
            'query': query,
            'stream': False,
            'chat_history': history}
    response = requests.post(url=url, headers=headers, data=json.dumps(data)).text
    print(f"ответ нейронки {response}")
    response = (json.loads(response))["messages"]
    print(f"messages {response}")
    print(type(response))
    return response

